'use strict';

const _ = require('lodash');
const fs = require('fs');
const http = require('http');

const server = http.createServer();

server.on('request', function(req, res) {
	fs.readFile('test.txt', function(err, text) {
		if (err) {
			console.error(err);
		}
		res.end(text);
	});
});

server.listen(1488);
